const Gmodules = require('../Gmodules.js');

////////////////////////////////////////////////////////////////////////////////
//
// 文字過濾器
//
// 使用方法
// Filter(string, 過濾器名字, 過濾器名字...);
//
////////////////////////////////////////////////////////////////////////////////

const Filter = {};

module.exports = Filter;
Gmodules["Filter"] = Filter;

(function () {
    const $f = Filter;

    // 分組
    $f.$addOnModuleGroups = {
        "*": {}
    };    
    //----------------------------

    $f.addModule = function (name, fun, moduleName) {
        if (typeof name != 'string') {
            throw new TypeError("args[0] type must be string");
        }
        if (typeof fun != 'function') {
            throw new TypeError("args[0] type must be function");
        }

        moduleName = (moduleName == null ? "*" : moduleName);

        if (this.$addOnModuleGroups[moduleName] == null) {
            this.$addOnModuleGroups[moduleName] = {};
        }

        this.$addOnModuleGroups[moduleName][name] = fun;
    };
    //----------------------------
    // engine: 傳遞 options
    $f.getModule = function (root, moduleName, module, name) {
        // debugger;

        const f = new FilterCore(root);

        moduleName = moduleName || "*";

        if (!(moduleName in this.$addOnModuleGroups)) {
            throw new Error(`no this moduleGroup(${moduleName}) in Fileter`);
        }

        const addOn = this.$addOnModuleGroups[moduleName]

        for (let k in addOn) {
            if (k in f) {
                // 避免 override
                throw new Error(`cant override Filter[${k}]`);
            }
            let fn = addOn[k];

            f[k] = function(text){
                return fn.call(f, text);
            };
        }

        module[name] = f;
    };

})();

///////////////////////////////////////////////////////////////////////////////
class FilterCore {
    constructor(root) {
        // debugger;

        Object.defineProperty(this, '$$$root',{
            value: root,
            configurable: false,
            writable: false,
            enumerable: false,

        });
    }
}
